sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }

    //Type appropriate comment here, and begin script below
    if (!newValue == '') {
        g_form.setSectionDisplay('medicine_details', true);
        g_form.setSectionDisplay('delivery_details', true);

        g_form.getReference('u_reference_1', getDetails);
    }
}

function getDetails(details) {
    g_form.setValue('u_model', details.model_number);
    g_form.setValue('u_medicine_description', details.short_description);
    g_form.setValue('u_price_3', details.u_price_2);
    g_form.setValue('u_warrenty_expiration', details.warranty_expiration);
}
